body.script:
        // RSS Feed 的完整 URL，将由 GitHub Pages 自动生成
        const rssUrl = './feed.xml'; 

        fetch(rssUrl)
            .then(response => response.text())
            .then(str => (new window.DOMParser()).parseFromString(str, "text/xml"))
            .then(data => {
                const channel = data.querySelector('channel');
                const episodesList = document.getElementById('episodes-list');

                // 1. 设置频道描述
                document.getElementById('channel-description').textContent = channel.querySelector('description').textContent;

                // 2. 遍历并渲染每一集
                const items = data.querySelectorAll('item');
                items.forEach(item => {
                    const title = item.querySelector('title').textContent;
                    const description = item.querySelector('description').textContent;
                    const audioUrl = item.querySelector('enclosure').getAttribute('url');

                    const episodeDiv = document.createElement('div');
                    episodeDiv.className = 'episode';
                    episodeDiv.innerHTML = `

${title}

${description}

                            您的浏览器不支持 audio 元素。

                    `;
                    episodesList.appendChild(episodeDiv);
                });
            })
            .catch(error => console.error('加载 RSS Feed 失败:', error));
